mapper.select:
SELECT 

FROM cms_tb_bid a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


  		AND a.STATUS = #{status}


		AND a.CREATE_DATE  >= #{beginDate} 


		AND a.CREATE_DATE  <= #{endDate} 


ORDER BY decode(status,1,1,4,2,2,3,3,4,5,5),update_date DESC, id DESC

SELECT
FROM cms_tb_bid a
a.del_flag = #{DEL_FLAG_NORMAL} and status != 1
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
AND a.STATUS = #{status}
AND a.CREATE_DATE >= #{beginDate}
AND a.CREATE_DATE <= #{endDate}
ORDER BY status,update_date DESC, id DESC

SELECT 

FROM cms_tb_bid a

	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_tb_bid SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE cms_tb_bid SET 
	STATUS = #{status}
	,update_date = sysdate

		,RELEASE_DATE = sysdate

WHERE id in

	 	#{id}

mapper.insert:
	insert into cms_tb_bid_txt(
		BID_ID,
    	BID_TXT
    )
	values(
		#{bidId},
		#{bidTxt}
	)

mapper.update:
    	update cms_tb_bid_txt SET
    		BID_TXT = #{bidTxt}
		WHERE BID_ID = #{bidId}
