mapper.select:
SELECT 

FROM cms_tb_sensitivity a
WHERE a.id = #{id}

SELECT
FROM cms_tb_sensitivity a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.sensitivity LIKE '%'||#{sensitivity}||'%' '%'+#{sensitivity}+'%' concat('%',#{sensitivity},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

	FROM cms_tb_sensitivity a

		a.del_flag = 0 and a.sensitivity = #{sensitivity}

SELECT

FROM cms_tb_sensitivity a
WHERE a.sensitivity = #{sensitivity}

SELECT 

FROM cms_tb_sensitivity a

	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_tb_sensitivity SET 	
	sensitivity = #{sensitivity},
	replacement = #{replacement},
	update_date = #{updateDate},
	update_by = #{updateBy.id}
WHERE id = #{id}

mapper.delete:
UPDATE cms_tb_sensitivity SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
